pageImage('#', 'https://bpptik.kominfo.go.id/wp-content/uploads/2021/07/xtot1-2-660x371.png.pagespeed.ic.FueswgsqIL.webp', 'Lap
                oran Ketua Panitia Penyelenggara oleh Kepala BPPTIK', 'Laporan Ketua Panitia Penyelenggara oleh Kepala BPPTIK');
